mapper.delete:
delete from `t_custom_package_order_detail`
where order_no = #{orderNo}
  and package_code = #{packageCode}

mapper.update:
update `t_custom_package_order_detail`
set custom_id = #{customId},
  package_name = #{packageName},
  package_price = #{packagePrice},
  count = #{count},
  create_time = #{createTime},
  del_flag = #{delFlag}
where order_no = #{orderNo}
  and package_code = #{packageCode}

mapper.select:
select order_no, package_code, custom_id, package_name, package_price, count, create_time, 
del_flag
from `t_custom_package_order_detail`
where order_no = #{orderNo}
  and package_code = #{packageCode}
