mapper.delete:
delete from `t_custom_package_relation`
where custom_id = #{customId}
  and package_code = #{packageCode}

mapper.select:
select custom_id, package_code, package_name, status, order_time, end_time, cancel_time, 
channel, opening_mode, create_time
from `t_custom_package_relation`
where custom_id = #{customId}
  and package_code = #{packageCode}
